settings.json:
{
    "editor.minimap.enabled": false,
    "window.zoomLevel": 1,
    "terminal.integrated.rendererType": "dom",
    "terminal.integrated.fontSize": 14,
    "files.exclude": {
        "**/p": true
    },
    "files.associations": { "*.module": "php" },
    "window.restoreWindows": "none",
    "extensions.autoCheckUpdates": false,
    "extensions.autoUpdate": false,
    "files.watcherExclude": {
        "**/p": true
    },
    "editor.wordWrap": "wordWrapColumn",
    "editor.fontFamily": "Fira Code",
    "editor.fontLigatures": true,
    "editor.rulers": [80, 120],
    "editor.renderLineHighlight": "gutter", /* move a borda de destaque de toda a linha para somente em torno do número da linha */
    "editor.tabSize": 2,
    "emmet.includeLanguages": {
        "javascript": "javascriptreact"
    },
    "emmet.syntaxProfiles": {
        "javascript": "jsx"
    },
    "javascript.updateImportsOnFileMove.enabled": "never", //* evita alterações automática do caminho de importações após mover um arquivo */
    "editor.parameterHints.enabled": false,
    "breadcrumbs.enabled": true,
    "javascript.suggest.autoImports": false,
    //"php.validate.executablePath": "/usr/local/bin/php72fpm",
    "workbench.colorTheme": "Dracula",
    "workbench.iconTheme": "material-icon-theme",
    "diffEditor.ignoreTrimWhitespace": false,
    "editor.codeActionsOnSave": {
        "source.fixAll.eslint": true
    },
    "telemetry.enableTelemetry": false,
    "intelephense.telemetry.enabled": false

}

keybindings.json:
// Place your key bindings in this file to override the defaultsauto[]
[
    {
        "key": "ctrl+;",
        "command": "editor.action.transformToLowercase"
    },
    {
        "key": "ctrl+[Quote]",
        "command": "editor.action.transformToUppercase"
    },
    {
        "key": "ctrl+k",
        "command": "editor.action.moveSelectionToNextFindMatch",
        "when": "editorFocus"
    },
    {
        "key": "ctrl+k ctrl+d",
        "command": "-editor.action.moveSelectionToNextFindMatch",
        "when": "editorFocus"
    },
    {
        "key": "ctrl+i",
        "command": "editor.action.addSelectionToNextFindMatch",
        "when": "editorFocus"
    },
    {
        "key": "ctrl+d",
        "command": "-editor.action.addSelectionToNextFindMatch",
        "when": "editorFocus"
    }
]
